import {View, Text, Image, StyleSheet, FlatList} from 'react-native'

export default function ImagePicker(props){

    const SCREEN_WIDTH=200;

    var data={
        "image": "/media_data/point_of_interest/boboli/eye_level_1.jpg",
        "image": "/media_data/point_of_interest/boboli/establish_1.jpg",
        "image": "/media_data/point_of_interest/uffizi/eye_level_1.jpg"
    }

    return (
        <View>
            <Text> My List! {props.name} </Text>
            <FlatList 
                horizontal
                snapToInterval={SCREEN_WIDTH}
                snapToAlignment="center"
                decelerationRate={'fast'}
                data={data}
                renderItem={({item}) => {
                    return (<View style={{width: SCREEN_WIDTH, height:30, backgroundColor: "red"}}>
                        <Image source={{ uri: item.image }} />
                        <Text> {item} </Text>
                    </View>)
                }}

            />
            
        </View>
    )
}
