import { StatusBar } from 'expo-status-bar';
import React, {useState, useEffect} from 'react';
import { StyleSheet, Text, View, Button, TextInput } from 'react-native';


function generate_url(poi_name, poi_image_name, action_name, action_shot_type, age, gender, other_details){
  return "http://127.0.0.1:8000/diffusers_api/generate_image?" + 
    "poi_name=" + poi_name + 
    "&poi_image_name=" + poi_image_name +
    "&action_name=" + action_name + 
    "&action_shot_type=" + action_shot_type +
    "&age=" + age + 
    "&gender=" + gender +
    "&other_details=" + other_details;
}

function GenerateImageForm(){
  const [poi_name, setPoiName] = useState("");
  const [loaded, setLoaded] = useState(false);

  // Gladdo: Definisco un'arrow function così che il this si riferisca allo stato della funzione in cui è definita l'arrow function
  const generate_image = () => {

  }

  useEffect(() => {
    fetch("http://127.0.0.1:8000/diffusers_api/test_serializer")
    .then( (response) =>  response.json())
    .then( 
      json => {
        setPoiName(json);
        console.log(poi_name);
        setLoaded(true);
      }
    )
  }, []);

  if(loaded){
    return (
      <View style={styles.form_container}>
        <Text style={styles.form_element}>Poi names: {poi_name} </Text>
        <Text style={styles.form_element}>Other Details </Text>
        <TextInput
          style={[styles.text_input, styles.form_element]}
          placeholder=" write here... "
          onChangeText={newText => setPoiName(newText)}
        />
        <View style={styles.generate_button}>
          <Button  title="GENERATE IMAGE" onPress={generate_image} > GENERATE </Button>
        </View>

      </View>
    )
  }else{
    return (
      <View>
        <Text> Loading Form ... </Text>
      </View>
    )
  }


}

export default function App() {
  return (
    <View style={styles.container}>
      <GenerateImageForm /> 
      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },

  form_container: {
    width: '80%',
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },

  form_element: {
    width: '100%',
    margin: 4,
  }, 

  text_input: {
    height: 40,
    borderWidth: 1,
    borderRadius: 4,
  },

  generate_button: {
    width: '80%',
    backgroundColor: 'red',
  }, 

});
