import {View, Text, Image, StyleSheet, FlatList, Button} from 'react-native'
import React, {useState} from 'react';

export default function GladImagePicker(props){
    // -------------------------|
    // FUNCTION STATE VARIABLES
    // -------------------------|
    const [flatList, setFlatList] = useState();
    const [currentIndex, setCurrentIndex] = useState(0);
    const [inner_width, setInnerWidth] = useState(0);

    // -------------------------|
    // CONST UTILITY VARIABLES
    // -------------------------|
    const imageHost = "http://127.0.0.1:8000/"
    const maxIndex = props.data.length - 1;
    const COMPONENT_WIDTH=props.width;

    // -------------------------|
    // EVENT FUNCTIONS
    // -------------------------|

    // GLaddo: Chiamata alla pressione del bottone di sinistra
    const previous_image = () =>{
        if(currentIndex>0){
            var index = currentIndex - 1;
            setCurrentIndex(index);
            flatList.scrollToIndex({animated: true, index: index});
            props.onSelection(props.data[index].name);
        }
    }

    // GLaddo: Chiamata alla pressione del bottone di destra
    const next_image = () =>{
        if(currentIndex<maxIndex){
            var index = currentIndex + 1;
            setCurrentIndex(index);
            flatList.scrollToIndex({animated: true, index: index});
            props.onSelection(props.data[index].name);
        }
    }

    // -------------------------|
    // RENDERING
    // -------------------------|
    return (
        <View style={{width: COMPONENT_WIDTH}} onLayout={(event) => {setInnerWidth(event.nativeEvent.layout.width)}}>
            
            {/* ------------------------------------------------------|
                FLATLIST A SCORRIMENTO ORIZZONTALE 
            |------------------------------------------------------ */}
            <FlatList 
                horizontal
                ref={(ref) => { setFlatList(ref); }}
                scrollEnabled={false}
                data={props.data}
                style={{width: '100%'}}
                renderItem={({item}) => {
                    /* FUNZIONE DI RENDERING DEL CONTAINER DI CIASCUNA IMMAGINE DELLA LISTA */
                    return (
                    <View style={{width: inner_width, justifyContent: 'center', alignItems: 'center'}}>
                        <Image style={{width: inner_width, height: inner_width}} resizeMode='cover' source={{ uri: imageHost + item.image }} />
                        <Text> {item.name} </Text>
                    </View>)
                }}
            />
            
            {/* ------------------------------------------------------|
                BUTTONS PER LA SELEZIONE 
            |------------------------------------------------------ */}
            <View style={{flexDirection: 'row', justifyContent: 'center'}}>
                <View style={{ width:"50%", alignItems: "flex-start" }}>
                    <Button title="<" onPress={previous_image}></Button>
                </View>
                <View style={{ width:"50%", alignItems: "flex-end" }}>
                    <Button title=">" onPress={next_image}></Button>
                </View>
            </View>

        </View>
    )
}


