import {View, Text, Image, StyleSheet, FlatList} from 'react-native'
import React, {useState} from 'react';
import { Button } from 'react-native-web';

export default function ImagePicker(props){
    const [flatList, setFlatList] = useState();
    const [currentIndex, setCurrentIndex] = useState(0);

    const imageHost = "http://127.0.0.1:8000/"

    const SCREEN_WIDTH=200;

    var data=[
        {"image": imageHost + "/media_data/point_of_interest/boboli/eye_level_1.jpg"},
        {"image": imageHost + "/media_data/point_of_interest/boboli/establish_1.jpg"},
        {"image": imageHost + "/media_data/point_of_interest/uffizi/eye_level_1.jpg"}
    ]

    const previous_image = () =>{
        setCurrentIndex(currentIndex-1);
        flatList.scrollToIndex({animated: true, index: currentIndex});
    }

    const next_image = () =>{
        setCurrentIndex(currentIndex+1);
        flatList.scrollToIndex({animated: true, index: currentIndex});
    }

    return (
        <View style={{width: SCREEN_WIDTH}}>
            <FlatList 
                horizontal
                ref={(ref) => { setFlatList(ref); }}
                scrollEnabled={false}
                
                data={data}
                renderItem={({item}) => {
                    return (
                    <View style={{width: SCREEN_WIDTH, justifyContent: 'center', alignItems: 'center'}}>
                        <Image style={{width: SCREEN_WIDTH, height: SCREEN_WIDTH}} resizeMode='cover' source={{ uri: item.image }} />
                        <Text> Name </Text>
                    </View>)
                }}

            />
            <View style={{flexDirection: 'row'}}>
                <Button title="L" onPress={previous_image}></Button>
                <Button title="R" onPress={next_image}></Button>
            </View>
        </View>
    )
}
