import {View, Text, Image, StyleSheet, FlatList, Button} from 'react-native'
import React, {useState} from 'react';

export default function GladImagePicker(props){
    const [flatList, setFlatList] = useState();
    const [currentIndex, setCurrentIndex] = useState(0);

    const imageHost = "http://127.0.0.1:8000/"
    var maxIndex = props.data.length - 1;

    const SCREEN_WIDTH=300;

    const previous_image = () =>{
        if(currentIndex>0){
            var index = currentIndex - 1;
            setCurrentIndex(index);
            flatList.scrollToIndex({animated: true, index: index});
            flatList.item
            props.SetParentIndex(props.data[index].name);
        }
    }

    const next_image = () =>{
        if(currentIndex<maxIndex){
            var index = currentIndex + 1;
            setCurrentIndex(index);
            flatList.scrollToIndex({animated: true, index: index});
            props.SetParentIndex(props.data[index].name);
        }
    }

    return (
        <View style={{width: SCREEN_WIDTH}}>
            <FlatList 
                horizontal
                ref={(ref) => { setFlatList(ref); }}
                scrollEnabled={false}
                
                data={props.data}
                renderItem={({item}) => {
                    return (
                    <View style={{width: SCREEN_WIDTH, justifyContent: 'center', alignItems: 'center'}}>
                        <Image style={{width: SCREEN_WIDTH, height: SCREEN_WIDTH}} resizeMode='cover' source={{ uri: imageHost + item.image }} />
                        <Text> {item.name} </Text>
                    </View>)
                }}
            />
            <View style={{flexDirection: 'row', justifyContent: 'center'}}>
                <View style={{ width:"50%", alignItems: "flex-start" }}>
                    <Button title="<" onPress={previous_image}></Button>
                </View>
                <View style={{ width:"50%", alignItems: "flex-end" }}>
                    <Button title=">" onPress={next_image}></Button>
                </View>
            </View>
        </View>
    )
}


