import { StatusBar } from 'expo-status-bar';
import React, {useState, useEffect} from 'react';
import { StyleSheet, Text, View, Button, TextInput, Picker } from 'react-native';
import GladImagePicker from './GladImagePicker'
import GladPicker from './GladPicker'


function generate_url(poi_name, poi_image_name, action_name, action_shot_type, age, gender, other_details){
  return "http://127.0.0.1:8000/diffusers_api/generate_image?" + 
    "poi_name=" + poi_name + 
    "&poi_image_name=" + poi_image_name +
    "&action_name=" + action_name + 
    "&action_shot_type=" + action_shot_type +
    "&age=" + age + 
    "&gender=" + gender +
    "&other_details=" + other_details;
}

function GenerateImageForm(){
  /* --------------------------------------------------------------------------------------------------------------------------------------------------- |
  > FUNCTION STATE VARIABLES:

  Le seguenti strutture definiscono lo stato associato alla funzione; attraverso useState, un pò come una classe,una funzione può avere uno stato!
  ----------------------------------------------------------------------------------------------------------------------------------------------- Gladdo */

  // Gladdo: Nelle variabili available_* viene inserito i dati ricevuti dal server inerenti ai rispettivi nomi 
  const [available_pois, setAvailablePois] = useState("");
  const [available_pois_images, setAvailablePoisImages] = useState("");

  // Gladdo: Nelle variabili selected_* viene inserito le scelte fatte dall'utente sul form
  const [selected_poi, setSelectedPoi] = useState("");
  const [selected_poi_image, setSelectedPoiImage] = useState("");

  // Gladdo: Variabile di stato a true se le variabili available_* sono state caricate
  const [loaded, setLoaded] = useState(false);

  /* --------------------------------------------------------------------------------------------------------------------------------------------------- |
  > FUNCTIONS:

  Funzioni varie per le features del front-end e l'interazione col back-end
  ----------------------------------------------------------------------------------------------------------------------------------------------- Gladdo */

  // -------------------------|
  // GENERATE IMAGE:
  // -------------------------|
  // Gladdo: Definisco un'arrow function così che il this si riferisca allo stato della funzione in cui è definita l'arrow function
  const generate_image = () => {

  }

  // -------------------------|
  // USE EFFECT:
  // -------------------------|
  /* Gladdo: Questa funzione viene chiamata da React-Native solo la prima volta che GenerateImageForm viene renderizzato.
  La funzione adempie alle seguenti cose:
    > Richiede i dati dal back-end con cui popolare le features del front-end che poi l'utente dovrà selezionare.
    > All'arrivo dei dati produce dei side effects sulle variabili di stato della funzione; questo triggera il re-rendering del componente che
    viene ri-renderizzato utilizzando i dati fetchati. */
  useEffect(() => {
    fetch("http://127.0.0.1:8000/diffusers_api/test_serializer")
    .then( (response) =>  response.json())
    .then( 
      json => {
        setAvailablePois(json['pois']);
        console.log(json['pois']);
        setAvailablePoisImages(json['poi_images'])
        console.log(json['poi_images'])
        setLoaded(true);
      }
    )
  }, []);

  // -------------------------|
  // EVENT POI SELECTION:
  // -------------------------|
  // Funzione triggerata quando l'utente seleziona il Point of Interest
  const eventPoiSelection = (name) => {
    setSelectedPoi(name);
    // Gladdo: Alla selezione del POI voglio assicurarmi che intanto venga selezionata la prima immagine disponibile, poi l'utente se vuole la cambia
    for(var i = 0; i < available_pois_images.length; i++){
      if(available_pois_images[i].poi == name){
        setSelectedPoiImage(available_pois_images[i].name);
        break;
      }
    } 
  }

  /* --------------------------------------------------------------------------------------------------------------------------------------------------- |
  > PRE-RENDERING SETUP:

  ----------------------------------------------------------------------------------------------------------------------------------------------- Gladdo */
  var poi_images_data = [];
  for(var i = 0; i < available_pois_images.length; i++){
    if(available_pois_images[i].poi == selected_poi){
      poi_images_data.push(available_pois_images[i]);
    }
  }  

  /* --------------------------------------------------------------------------------------------------------------------------------------------------- |
  > RENDERING:

  ----------------------------------------------------------------------------------------------------------------------------------------------- Gladdo */

  if(loaded){
    return (
      <View style={styles.form_container}>

        <GladPicker data={available_pois} SetSelectedItem={eventPoiSelection}/>
        <GladImagePicker 
          SetSelectedImageName={ 
            (name) => {
              setSelectedPoiImage(name);
              console.log(name);
            }          
          } 
          data={poi_images_data}
        />
        <Text style={styles.form_element}>Other Details </Text>
        <TextInput
          style={[styles.text_input, styles.form_element]}
          placeholder=" write here... "
          onChangeText={newText => setPoiName(newText)}
        />
        <View style={styles.generate_button}>
          <Button  title="GENERATE IMAGE" onPress={generate_image} > GENERATE </Button>
        </View>

      </View>
    )
  }else{
    return (
      <View>
        <Text> Loading Form ... </Text>
      </View>
    )
  }


}

export default function App() {
  return (
    <View style={styles.container}>
      <GenerateImageForm /> 

      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },

  form_container: {
    width: '80%',
    height: 200,
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },

  form_element: {
    width: '100%',
    margin: 4,
  }, 

  text_input: {
    height: 40,
    borderWidth: 1,
    borderRadius: 4,
  },

  generate_button: {
    width: '80%',
    backgroundColor: 'red',
  }, 

});



