import {View, Animated, ScrollView, Modal, Text, Image, StyleSheet, FlatList, Button} from 'react-native'
import React, {useState, useRef, useEffect} from 'react';
import { TouchableOpacity } from 'react-native-web';

function PopView(prop){
    if(!prop.visible){
        return
    }

    return (<View> 

    </View>)
}

export default function GladPicker(props){
    var heightAnim = useRef(new Animated.Value(0)).current;
    var textHeightAnim = useRef(new Animated.Value(0)).current;;
    var [selectedItem, SetSelectedItem] = useState("Select a Poi");
    const [menuVisible, SetMenuVisibility] = useState(false);

    const selection = (itemId) => {
        SetMenuVisibility(data[itemId].value);
    }

    const menuPop = () => {
        var nextHeight;
        var nextTextHeight;
        if(menuVisible){
            nextHeight=0;
            nextTextHeight=0;
            SetMenuVisibility(false);
        }else{
            nextHeight=200;
            nextTextHeight=40;
            SetMenuVisibility(true);
        }
        Animated.timing(heightAnim, {
            toValue: nextHeight,
            duration: 200,
            useNativeDriver: true,
          }).start();
          Animated.timing(textHeightAnim, {
            toValue: nextTextHeight,
            duration: 200,
            useNativeDriver: true,
          }).start();
    };

    var data = [ {"value": "item1"},{"value": "item2"},{"value": "item3"},{"value": "item4"},{"value": "item5"},{"value": "item6"},{"value": "item7"},{"value": "item8"},{"value": "item9"},{"value": "item10"},{"value": "item11"}];
    
    var menuItems = [];
    if(menuVisible){
        for(var i = 0; i < data.length; i ++){
            menuItems.push(
                <TouchableOpacity key={i} onPress={selection(i)}>
                    <Animated.View style={{height: textHeightAnim, justifyContent: 'center', borderBottomWidth: 1}}>
                        <Animated.Text style={{height: textHeightAnim*0.5, textAlign:'center', fontWeight: 'bold', fontSize: 14}} > {data[i].value}</Animated.Text>
                    </Animated.View>
                </TouchableOpacity>
            )
        }
    }

    return(
        <View style={{ zIndex: 1, position: 'relative', height: 40, width: 300, }} >
            <TouchableOpacity style={{height:40, backgroundColor: "#258ef7"}} onPress={menuPop}>
                <View style={{height: '100%', justifyContent: 'center'}}>
                    <Text style={{ textAlign: 'center', color: 'white', fontWeight: 'bold', fontSize: 18}}> {selectedItem} </Text>
                </View> 
            </TouchableOpacity>
            <Animated.View style={{ marginTop: 40, zIndex: 2, position: 'absolute', width: '100%', height: heightAnim, backgroundColor: "#dcecfc", overflow: 'visible'}}>
                <ScrollView showsVerticalScrollIndicator={false}>
                    {menuItems}
                </ScrollView>
            </Animated.View>   
       </View>

    )
}
