import { StatusBar } from 'expo-status-bar';
import React, {useState, useEffect} from 'react';
import { StyleSheet, Text, View, Button, TextInput, Picker } from 'react-native';
import GladImagePicker from './GladImagePicker'


function generate_url(poi_name, poi_image_name, action_name, action_shot_type, age, gender, other_details){
  return "http://127.0.0.1:8000/diffusers_api/generate_image?" + 
    "poi_name=" + poi_name + 
    "&poi_image_name=" + poi_image_name +
    "&action_name=" + action_name + 
    "&action_shot_type=" + action_shot_type +
    "&age=" + age + 
    "&gender=" + gender +
    "&other_details=" + other_details;
}

function GenerateImageForm(){
  // Gladdo: Le seguenti strutture definiscono lo stato associato alla funzione; attraverso useState una funzione, un pò come una classe, può avere uno stato associato.
  const [available_pois, setAvailablePois] = useState("");
  const [available_pois_images, setAvailablePoisImages] = useState("");
  const [selected_poi, setSelectedPoi] = useState(0);
  const [loaded, setLoaded] = useState(false);

  const select_poi = (index) => {
    setSelectedPoi(index);
    console.log(index);
  }

  // Gladdo: Definisco un'arrow function così che il this si riferisca allo stato della funzione in cui è definita l'arrow function
  const generate_image = () => {

  }

  /* Gladdo: useEffect viene chiamata da React-Native solo la prima volta che il componente associato a GenerateImageForm viene renderizzato.
  Il corpo di useEffect fetcha dei dati da un server e utilizza tali dati per modificare lo stato della funzione; il cambiamento di stato triggera il re-rendering del componente.
  Nel re-rendering viene poi utilizzato i dati fetchati per popolare i campi del form. */
  useEffect(() => {
    fetch("http://127.0.0.1:8000/diffusers_api/test_serializer")
    .then( (response) =>  response.json())
    .then( 
      json => {
        setAvailablePois(json['pois']);
        console.log(json['pois']);
        setAvailablePoisImages(json['poi_images'])
        console.log(json['poi_images'])
        setLoaded(true);
      }
    )
  }, []);

  /* Gladdo: Itera sui pois fetchati e ci costruisce un componente */
  var poi_names = [];

  for(var i = 0; i < available_pois.length; i++){
    poi_names.push(
      <Text key={i}>
        {available_pois[i].name}
      </Text>
    );
  }

  if(loaded){
    return (
      <View style={styles.form_container}>
        <Text style={styles.form_element}>Poi names:  </Text>
        {poi_names}
        <GladImagePicker SetParentIndex={select_poi} data={available_pois_images}/>
        <Text style={styles.form_element}>Other Details </Text>
        <TextInput
          style={[styles.text_input, styles.form_element]}
          placeholder=" write here... "
          onChangeText={newText => setPoiName(newText)}
        />
        <View style={styles.generate_button}>
          <Button  title="GENERATE IMAGE" onPress={generate_image} > GENERATE </Button>
        </View>

      </View>
    )
  }else{
    return (
      <View>
        <Text> Loading Form ... </Text>
      </View>
    )
  }


}

export default function App() {
  return (
    <View style={styles.container}>
      <GenerateImageForm /> 

      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },

  form_container: {
    width: '80%',
    height: 200,
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },

  form_element: {
    width: '100%',
    margin: 4,
  }, 

  text_input: {
    height: 40,
    borderWidth: 1,
    borderRadius: 4,
  },

  generate_button: {
    width: '80%',
    backgroundColor: 'red',
  }, 

});



