import React, {useState, useEffect} from 'react';
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, View, Button } from 'react-native';
import GenerateImageForm from './GenerateImageForm'
import CreateLoraForm from './CreateLoraForm'
import {Dimensions} from 'react-native';

/* --------------------------------------------------------------------------------------------------------------------------------------------------- |
> APP HOOK:
----------------------------------------------------------------------------------------------------------------------------------------------- Gladdo */

export default function App() {
  const [page, setPage] = useState(1);

  if(page==1){
    return(
      <View style={styles.container}>
        <View style={styles.button}>
            <Button  title="GENERATE IMAGE" onPress={() => {setPage(2)}} > GENERATE </Button>
        </View>
        <View style={styles.button}>
            <Button  title="CREATE YOUR LORA" onPress={() => {setPage(2)}} > CREATE </Button>
        </View>
      </View>);
  }
  if(page==2){
    return(
    <View style={styles.container}>
      <GenerateImageForm />
      <StatusBar style="auto" />
    </View>);
  }
  if(page==3){
    return(
      <View style={styles.container}>
        <CreateLoraForm />
        <StatusBar style="auto" />
      </View>);
  }
}
