import {View, Text, Image, StyleSheet, FlatList} from 'react-native'

export default function ImagePicker(props){

    const imageHost = "http://127.0.0.1:8000/"

    const SCREEN_WIDTH=200;

    var data=[
        {"image": imageHost + "/media_data/point_of_interest/boboli/eye_level_1.jpg"},
        {"image": imageHost + "/media_data/point_of_interest/boboli/establish_1.jpg"},
        {"image": imageHost + "/media_data/point_of_interest/uffizi/eye_level_1.jpg"}
    ]

    return (
        <View>
            <Text> My List! {props.name} </Text>
            <FlatList 
                horizontal
                snapToInterval={SCREEN_WIDTH}
                snapToAlignment="center"
                decelerationRate={'fast'}
                data={data}
                renderItem={({item}) => {
                    return (<View style={{width: SCREEN_WIDTH, height: SCREEN_WIDTH}}>
                        <Image style={{width: "100%", height: "100%", resizeMode: 'cover'}}source={{ uri: item.image }} />
                        <Text> Image </Text>
                    </View>)
                }}

            />
            
        </View>
    )
}
