  /* <FormData submit={generate_image}>
     </FormData> */
  return (
    <View style={styles.form_container}>
      <Text style={styles.form_element}>Other Details </Text>
      <TextInput
        style={[styles.text_input, styles.form_element]}
        placeholder="poi_name"
        onChangeText={newText => setPoiName(newText)}
        defaultValue={" write " + poi_name}
      />
      <Pressable style={[styles.generate_button, styles.form_element]} onPress={generate_image} ></Pressable>     
    </View>
  )

}

export default function App() {
  return (
    <View style={styles.container}>
      <GenerateImageForm /> 
      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },

  form_container: {
    width: '80%',
